mapper.select:
select count(*)
from storage.user
where user_id=#{user_id}

SELECT DISTINCT station_name, station_id 
FROM storage.local 
WHERE line_name = #{line_name}


select *
from storage.user
where user_id=#{user_id} and user_pw=#{user_pw}

mapper.update:
UPDATE storage.user
SET auth = 4
WHERE user_id = #{user_id}

UPDATE storage.user
SET auth = 2
WHERE user_id = #{user_id}

mapper.select:
SELECT user_id, user_name, line_name, station_name, auth FROM storage.user WHERE auth != '3'
AND (user_id LIKE CONCAT('%', #{searchKeyword}, '%') OR user_name LIKE CONCAT('%', #{searchKeyword}, '%'))
ORDER BY CASE WHEN auth = '1' THEN 0 ELSE 1 END ASC, CASE WHEN auth = '4' THEN 1 ELSE 0 END ASC, user_id DESC LIMIT 5 OFFSET #{offset}

mapper.update:
UPDATE storage.user
SET auth = 2  WHERE user_id IN

    #{id}

UPDATE storage.user
SET auth = 4  WHERE user_id IN

    #{id}

UPDATE storage.user
SET auth = #{auth}

WHERE 
    user_id = #{user_id}
